INSERT INTO `code_learn` (`id`, `code`, `question`, `request`, `example`, `created_at`, `updated_at`) VALUES
(1, 'exercise-45309', 'Tìm một cặp khi biết tổng của cặp đó trong mảng.', 'Cho một mảng không sắp xếp các số nguyên, tìm một cặp khi biết tổng của cặp đó.\r\n\r\n<b>Ví dụ:</b>\r\n\r\n<b>Đầu vào:</b> <code>arr = [8, 7, 2, 5, 3\r\n, 1]</code> và <code>sum = 10</code>\r\n<b>Đầu ra:</b> <code>Tìm thấy cặp ở ví trí phần tử thứ 0 và 2 (8 +2)</code> hoặc <code>Tìm thấy cặp ở phần tử thứ 1 và 4 ( 7 + 3)</code>', 'class Main\r\n{\r\n    // Naive method to find a pair in an array with given sum\r\n    public static void findPair(int[] A, int sum)\r\n    {\r\n        // consider each element except last element\r\n        for (int i = 0; i < A.length - 1; i++)\r\n        {\r\n            // start from i\'th element till last element\r\n            for (int j = i + 1; j < A.length; j++)\r\n            {\r\n                // if desired sum is found, print it and return\r\n                if (A[i] + A[j] == sum)\r\n                {\r\n                    System.out.println(\"Pair found at index \" + i + \" and \" + j);\r\n                    return;\r\n                }\r\n            }\r\n        }\r\n \r\n        // No pair with given sum exists in the array\r\n        System.out.println(\"Pair not found\");\r\n    }\r\n \r\n    public static void main (String[] args)\r\n    {\r\n        int[] A = { 8, 7, 2, 5, 3, 1 };\r\n        int sum = 10;\r\n \r\n        findPair(A, sum);\r\n    }\r\n}', NULL, NULL),
(2, 'exercise-92138', 'Tìm tất cả các mảng con có tổng bằng 0', 'Cho 1 mảng các số nguyên, in tất cả các mảng con có tổng bằng 0\r\n\r\n<b>Ví dụ:</b>\r\nInput: <code>arr = {4, 2, -3, -1, 0, 4}</code>\r\nMảng con có tổng 0 là:\r\n<code>{-3, -1, 0, 4}\r\n{0}</code>\r\n\r\nInput: <code>{3, 4, -7, 3, 1, 3,1, -4, -2, -2}</code>\r\nMảng con có tổng 0 là:\r\n<code>\r\n{3, 4, -7}\r\n{4, -7, 3}\r\n{-7, 3, 1, 3}\r\n{3, 1, -4}\r\n{3, 1, 3, 1, -4, -2, -2}\r\n{3, 4, -7, 3, 1, 3, 1, -4, -2, -2}\r\n</code>\r\n\r\nOutput:\r\n<code>\r\nsubarray [0..2]\r\nsubarray [0..9]\r\nsubarray [1..3]\r\nsubarray [2..5]\r\nsubarray [3..9]\r\nsubarray [5..7]\r\n</code>', 'class Main\r\n{\r\n    // Function to print all sub-arrays with 0 sum present\r\n    // in the given array\r\n    public static void printallSubarrays(int[] A)\r\n    {\r\n        // consider all sub-arrays starting from i\r\n        for (int i = 0; i < A.length; i++)\r\n        {\r\n            int sum = 0;\r\n \r\n            // consider all sub-arrays ending at j\r\n            for (int j = i; j < A.length; j++)\r\n            {\r\n                // sum of elements so far\r\n                sum += A[j];\r\n \r\n                // if sum is seen before, we have found a subarray with 0 sum\r\n                if (sum == 0) {\r\n                    System.out.println(\"Subarray [\" + i + \"..\" + j + \"]\");\r\n                }\r\n            }\r\n        }\r\n    }\r\n \r\n        public static void main (String[] args)\r\n    {\r\n        int[] A = { 3, 4, -7, 3, 1, 3, 1, -4, -2, -2 };\r\n \r\n        printallSubarrays(A);\r\n    }\r\n}', NULL, NULL),
(3, 'exercise-53482', 'Sắp xếp mảng nhị phân theo thời gian tuyến tính', 'Cho một mảng nhị phân, hãy sắp xếp nó theo thời gian tuyến tính và không gian đổi.\r\nĐầu ra nên in các số 0 theo sau là các số 1\r\n\r\nInput:\r\n<code>\r\n{1, 0, 1, 0, 1, 0, 0, 1}\r\n</code>\r\nOutput:\r\n<code>\r\n{0, 0, 0, 0, 1, 1, 1, 1}\r\n</code>', 'import java.util.Arrays;\r\n \r\nclass Main\r\n{\r\n    // Function to sort binary array in linear time\r\n    public static void sort(int[] A)\r\n    {\r\n        // count number of 0\'s\r\n        int zeros = 0;\r\n        for (int value : A) {\r\n            if (value == 0) {\r\n                zeros++;\r\n            }\r\n        }\r\n \r\n        // put 0\'s in the beginning\r\n        int k = 0;\r\n        while (zeros-- != 0) {\r\n            A[k++] = 0;\r\n        }\r\n \r\n        // fill all remaining elements by 1\r\n        while (k < A.length) {\r\n            A[k++] = 1;\r\n        }\r\n    }\r\n \r\n    // Sort binary array in linear time\r\n    public static void main (String[] args)\r\n    {\r\n        int[] A = { 0, 0, 1, 0, 1, 1, 0, 1, 0, 0 };\r\n \r\n        sort(A);\r\n \r\n        // print the rearranged array\r\n        System.out.println(Arrays.toString(A));\r\n    }\r\n}', NULL, NULL),
(4, 'exercise-34698', 'Thuật toán Quickselect', 'Quickselect là một thuật toán lựa chọn để tìm phần tử nhỏ nhất thứ k trong 1 danh sách không có thứ tự.\r\nNó liên quan chặt chẽ đến thuật toán sắp xếp nhanh. Giống như quicksort, nó có hiệu quả trong thực tế va có hiệu suất trong trường hợp trung bình tốt, nhưng có hiệu suất trong trường hợp xấu kém nhất\r\n\r\nInput:\r\n<code>\r\narr = [7, 4, 6, 3, 9, 1]\r\nk = 2\r\n</code>\r\nOutput:\r\n<code>\r\nk\'th smallest element in the array is 4\r\n</code>\r\n\r\nInput:\r\n<code>\r\narr = [7, 4, 6, 3, 9, 1]\r\nk = 0 (k bắt đầu từ 0)\r\n</code>\r\nOutput:\r\n<code>\r\nk\'th smallest element in the array is 1\r\n</code>', 'import java.util.Random;\r\n \r\nclass Main\r\n{\r\n    public static int rand(int min, int max)\r\n    {\r\n        if (min > max || (max - min + 1 > Integer.MAX_VALUE)) {\r\n            throw new IllegalArgumentException(\"Invalid range\");\r\n        }\r\n        return new Random().nextInt(max - min + 1) + min;\r\n    }\r\n \r\n    public static void swap(int[] A, int i, int j)\r\n    {\r\n        int temp = A[i];\r\n        A[i] = A[j];\r\n        A[j] = temp;\r\n    }\r\n \r\n    // Partition using Lomuto partition scheme\r\n    public static int partition(int[] A, int left, int right, int pivotIndex)\r\n    {\r\n        // Pick pivotIndex as pivot from the array\r\n        int pivot = A[pivotIndex];\r\n \r\n        // Move pivot to end\r\n        swap(A, pivotIndex, right);\r\n \r\n        // elements less than pivot will be pushed to the left of pIndex\r\n        // elements more than pivot will be pushed to the right of pIndex\r\n        // equal elements can go either way\r\n        int pIndex = left;\r\n \r\n        // each time we finds an element less than or equal to pivot, pIndex\r\n        // is incremented and that element would be placed before the pivot.\r\n        for (int i = left; i < right; i++)\r\n        {\r\n            if (A[i] <= pivot)\r\n            {\r\n                swap(A, i, pIndex);\r\n                pIndex++;\r\n            }\r\n        }\r\n \r\n        // Move pivot to its final place\r\n        swap(A, pIndex, right);\r\n \r\n        // return pIndex (index of pivot element)\r\n        return pIndex;\r\n    }\r\n \r\n    // Returns the k-th smallest element of list within left..right\r\n    // (i.e. left <= k <= right). The search space within the array is\r\n    // changing for each round - but the list is still the same size.\r\n    // Thus, k does not need to be updated with each round.\r\n    public static int quickSelect(int[] A, int left, int right, int k)\r\n    {\r\n        // If the array contains only one element, return that element\r\n        if (left == right) {\r\n            return A[left];\r\n        }\r\n \r\n        // select a pivotIndex between left and right\r\n        int pivotIndex = rand(left, right);\r\n \r\n        pivotIndex = partition(A, left, right, pivotIndex);\r\n \r\n        // The pivot is in its final sorted position\r\n        if (k == pivotIndex) {\r\n            return A[k];\r\n        }\r\n \r\n        // if k is less than the pivot index\r\n        else if (k < pivotIndex) {\r\n            return quickSelect(A, left, pivotIndex - 1, k);\r\n        }\r\n \r\n        // if k is more than the pivot index\r\n        else {\r\n            return quickSelect(A, pivotIndex + 1, right, k);\r\n        }\r\n    }\r\n \r\n    public static void main(String[] args)\r\n    {\r\n        int[] A = { 7, 4, 6, 3, 9, 1 };\r\n        int k = 2;\r\n \r\n        System.out.print(\"K\'th smallest element is \"  +\r\n                quickSelect(A, 0, A.length - 1, k));\r\n    }\r\n}', NULL, NULL),
(5, 'exercise-64219', 'Tìm chỉ số tối thiểu của phần tử lặp lại trong một mảng', 'Cho một mảng các số nguyên, tìm chỉ số tối thiểu của phần tử lặp lại theo thời gian tuyến tính và chỉ thực hiện một lần duyệt của mảng.\r\n\r\nVí dụ:\r\nInput:\r\n<code>\r\n{5, 6, 3, 4, 3, 6, 4, }\r\n</code>\r\nOutput: Minimum index of repeating element is 1\r\n\r\nInput:\r\n<code>\r\n{1, 2, 3, 4, 5, 6 }\r\n</code>\r\nOutput: invalid input', 'import java.util.HashSet;\r\nimport java.util.Set;\r\n \r\nclass Main\r\n{\r\n    // Function to find minimum index of repeating element\r\n    public static int findMinIndex(int[] A)\r\n    {\r\n        int minIndex = A.length;\r\n \r\n        // create an empty set to store array elements\r\n        Set<Integer> set = new HashSet<>();\r\n \r\n        // traverse array from right to left\r\n        for (int i = A.length - 1; i >= 0; i--)\r\n        {\r\n            // if the element is seen before, update minimum index\r\n            if (set.contains(A[i])) {\r\n                minIndex = i;\r\n            }\r\n            // if the element is seen for the first time, insert it into set\r\n            else {\r\n                set.add(A[i]);\r\n            }\r\n        }\r\n \r\n        // return minimum index\r\n        return minIndex;\r\n    }\r\n \r\n    public static void main(String[] args)\r\n    {\r\n        int[] A = { 5, 6, 3, 4, 3, 6, 4 };\r\n \r\n        int minIndex = findMinIndex(A);\r\n \r\n        if (minIndex != A.length) {\r\n            System.out.print(\"Minimum index of repeating element is \" + minIndex);\r\n        }\r\n        else {\r\n            System.out.print(\"Invalid Input\");\r\n        }\r\n    }\r\n}', NULL, NULL);
